import React, { useState } from 'react';
import { useQuestions } from './questions';

export default function NewQuestion() {

  const [question, setQuestion] = useState('');
  const [qTitle, setQTitle] = useState('');
  const { addQuestion } = useQuestions();


  const submit = e => {
    e.preventDefault()
    if (e.keyCode === 13) {
      console.log('enter')
  }
    addQuestion(question)
    setQuestion('')
  }

  return (
    <>
      <div>
        <input type="text" value={qTitle} onChange={e => setQTitle(e.target.value)} placeholder="What is the value of Ï€?"/>
        <br />
        <input type="text" value={question} placeholder="Type an answer" onChange={e => setQuestion(e.target.value)} required />
        <button onClick={submit}>Add</button>
      </div>
    </>

  )
}
