import { useQuestions } from './questions';

export default function NewQuestion() {

  const [question, setQuestion] = useState('');
  const [qTitle, setQTitle] = useState('');
  const { addQuestion, addQuestionTitle } = useQuestions();

  let idx = 0;
  const submit = e => {
    e.preventDefault();
    addQuestion(question);
    console.log(idx = idx+1)
    setQuestion('');
  }

  const handleChange = e => {
   e.preventDefault(); 
   setQTitle(e.target.value);
   addQuestionTitle(e.target.value);
  }

  return (
    <>
      <div>
        <input type="text" value={qTitle} onChange={handleChange} placeholder="What is the value of Ï€?"/>
        <br />
        <input type="text" value={question} placeholder="Type an answer" onChange={e => setQuestion(e.target.value)} required />
        <button onClick={submit}>Add</button>
      </div>
    </>

  )
}
