import React, { useState } from 'react';
import { useQuestions } from './questions';

export default function Question({ question }) {

  const [change, setChange] = useState([]);
  const { addQuestion, removeQuestion, updateQuestions } = useQuestions();

  const remove = e => {
    e.preventDefault();

    removeQuestion(question);
  }

  const update = e => {
    e.preventDefault();
    console.log(change)
    updateQuestions(change)
  }

  return (
    <>
    <div>
      <ul>
        <li><span className="question"><input type="text" value={question} onChange={e => setChange(e.target.value)} /></span><button onClick={remove}> Remove</button></li>
      </ul>
    </div>
    </>
  )
}
