import React, {useEffect} from 'react';  
import Highcharts from 'highcharts';
import './result.scss';
import { useQuestions } from '../questionsComponent/questions';

function Result() {

    const { votes } = useQuestions();



    const count = {};
    votes.forEach(function(i) { count[i] = (count[i]||0) + 1;});

    let xAxis = Object.keys(count),
        yAxis = Object.values(count),
        seperatedVotes = [yAxis]

    console.log(Object.keys(count),Object.values(count))


    // const config = {
    //     chart: {
    //         type: 'column',
    //         width: 500,
    //         height:500
    //     },
    //     title: {
    //         text: 'Vote Results'
    //     },
    //     xAxis: {
    //         categories: xAxis
    //     },
    //     legend:{
    //         enabled:false
    //     },
    //     series: seperatedVotes
    // };

    useEffect(() => {
        Highcharts.chart('container', {
            chart: {
                type: 'column',
                width: 500,
                height: 500,
            },
            title: {
                text: 'Monthly Average Rainfall'
            },
            subtitle: {
                text: 'Source: WorldClimate.com'
            },
            xAxis: {
                categories: xAxis,
                crosshair: true
            },
            yAxis: {
                min: 0,
                title: {
                    text: 'Rainfall (mm)'
                }
            },
            plotOptions: {
                column: {
                    pointPadding: 0.2,
                    borderWidth: 0
                }
            },
            series: [{
                data: seperatedVotes

            }]
        });
    })

    console.log('asd', seperatedVotes)
    //console.log(config)

    return <div className="result-wrapper">
        <div id="container"></div>
    </div>
}


export default Result;
