import React, {createContext, useState, useContext} from 'react'
import './questions.scss';

const QuestionContext = createContext()
export const useQuestions = () => useContext(QuestionContext)

export default function QuestionProvider({ children }) {

    const [questions, setQuestions] = useState([])

    const addQuestion = question =>

      setQuestions([
          ...questions,
          { 
              question
          }
      ])

    const removeQuestion = question => {

        let removed = questions.filter((questionArr) => {

                if(questionArr.question !== question) return questionArr;

            });

            setQuestions(removed);

    };

    const resetQuestionList = () =>{
        setQuestions([])
    }

  return (
      <QuestionContext.Provider value={{ questions, addQuestion, removeQuestion }}>
          { children }
          <span>{questions.length} / 10 possible answers</span> <button onClick={resetQuestionList}>Reset</button>
      </QuestionContext.Provider>
  )
}
