import React from 'react'
import { useQuestions } from './questions'

export default function Question({ id, question, complete }) {
  const { setStatusQuestion } = useQuestions()

  const checkQuestion = e => setStatusQuestion(id, e.target.checked)

  return (
    <tr>
      <td>
        <input type="checkbox"  onChange={checkQuestion} />
      </td>
      <td>
        <span className={ complete ? 'task-done' : '' }>{ task }</span>
      </td>
    </tr>
  )
}
