import React, {createContext, useState, useContext} from 'react'
import './questions.scss';

const QuestionContext = createContext()
export const useQuestions = () => useContext(QuestionContext)

export default function QuestionProvider({ children }) {

    const [questions, setQuestions] = useState([]);
    const [qTitle, setQTitle] = useState('');

    const addQuestionTitle = qTitle =>
        setQTitle([
            ...qTitle
        ]);

    const addQuestion = question =>

      setQuestions([
          ...questions,
          { 
              question
          }
      ])

    const removeQuestion = question => {

        let removed = questions.filter((questionArr) => {

                if(questionArr.question !== question) return questionArr;

            });

            setQuestions(removed);

    };

    let updateQuestionIndex;
    const updateQuestions = question => {
        questions.filter((questionArr) => {

            updateQuestionIndex = questions.findIndex(o => o.question === questionArr.question);

            questions[updateQuestionIndex].question = {question: questionArr.question};

            console.log(questions[updateQuestionIndex].question)
        })


    }

    const resetQuestionList = () =>{
        setQuestions([])
    }

  return (
      <QuestionContext.Provider value={{ questions, qTitle, addQuestionTitle, addQuestion, removeQuestion, updateQuestions }}>
          { children }
          <span>{questions.length} / 10 possible answers</span> <button onClick={resetQuestionList}>Reset</button>
      </QuestionContext.Provider>
  )
}
