import React, { useEffect, useState } from 'react';
import { useQuestions } from './questions';

export default function Question({ id, question }) {

  const [change, setChange] = useState([]);
  const { addQuestion, removeQuestion, updateQuestions } = useQuestions();



  const remove = e => {
    e.preventDefault();

    removeQuestion(id,question);
  }

  useEffect(() => {
    setChange([
      ...change,
      { 
          id,
          question
      }
    ])
  })

  const handleChange = e => {
    e.preventDefault(); 
    setChange(e.target.value);
    updateQuestions(change)
    //console.log(change)
   }

  return (
    <>
    <div>
      <ul>
        <li><span className="question"><input type="text" value={change.question} maxLength="80" onChange={handleChange} /></span><button onClick={remove}> x</button></li>
      </ul>
    </div>
    </>
  )
}
