import React from 'react';  
import ReactHighcharts from 'react-highcharts';
import './result.scss';
import { useQuestions } from '../questionsComponent/questions';

function Result() {

    const { votes } = useQuestions();



    const count = {};
    votes.forEach(function(i) { count[i] = (count[i]||0) + 1;});

    let seperatedVotes = []

    console.log(Object.keys(count),Object.values(count))

    const config = {
        chart: {
            type: 'bar'
        },
        title: {
            text: 'Vote Results'
        },
        xAxis: {
            categories: Object.values(count)
        },
        yAxis: {
            categories: Object.keys(count)
        },
        series: seperatedVotes
    };

    console.log(config)

    return <div className="result-wrapper">
        <ReactHighcharts config = {config}></ReactHighcharts>
    </div>
}


export default Result;
