import React, {useState} from 'react';
import { useQuestions } from '../questionsComponent/questions';
import './vote.scss';


const Vote = ({ id, question }) => {

  console.log(id, question)
  const { questions, qTitle } = useQuestions()

  let questionTitle = qTitle;


  return ( 
    <div>
        <h3>{questionTitle}</h3>
        <div key={id} className="vote-option">
          <input id={id} name="vote" type="radio"/>
          <label htmlFor={id}>{question}</label>
        </div>

    </div>
  )
}



export default Vote;
