import React, { useEffect, useState } from 'react';
import { useQuestions } from './questions';

export default function Question({ id, question }) {

  const [change, setChange] = useState([]);
  const { addQuestion, removeQuestion, updateQuestions } = useQuestions();



  const remove = e => {
    e.preventDefault();

    removeQuestion(id,question);
  }

  useEffect(() => {
    setChange(id, question)
  })

  const handleChange = e => {
    e.preventDefault(); 
    setChange(e.target.value);
    updateQuestions(id,change)
    console.log(id ,change)
   }

  return (
    <>

        <li><span className="question"><input type="text" value={change[question]} id={id} maxLength="80" onChange={handleChange} /></span><button onClick={remove}> x</button></li>

    </>
  )
}
