import React, { useState, useRef, useReducer } from 'react';
import './questions.scss';

function Questions(){
    const initialState = '';
    const reducer = (state, action) => action;
    const [questionText, changeQuestionText] = useReducer(reducer, initialState);
    const [optionsText, changeOptionsText] = useReducer(reducer, initialState);
    const [voteQuestion, setVoteQuestion] = useState(null);
    const [voteOptions, setVoteOptions] = useState([]);
    const questionRef = useRef(null);
    const optionsRef = useRef(null);


    function addOption(){
        setVoteOptions(optionsText) 
    }

    return <div className="questions-wrapper">
        <QuestionInput type="text" placeholder="What is the value of π?" value={questionText} onChangeText={changeQuestionText} />
        <div className="options-wrapper">
        <input type="text" placeholder="Type an answer" maxLength="80" onChange={e => setVoteOptions(e.target.value)} />

            <div className="addOption" onClick={addOption}>Add</div>
        </div>
        <div className="options-list">
            {voteOptions}
        </div>
    </div>
}


const QuestionInput = ({ value, onChangeText }) => (
    <input type="text" value={value} placeholder="What is the value of π?" maxLength="80" onChange={e => onChangeText(e.target.value)} />
  );

const OptionsInput = ({value, onChangeText}) => (
    <input type="text" value={value} placeholder="Type an answer" maxLength="80" onChange={e => onChangeText(e.target.value)} />
);

export default Questions;
