import React, { useState } from 'react';
import { useQuestions } from './questions';

export default function NewQuestion() {

  const [qCount, setQCount] = useState(0);
  const [typeLimit, setTypeLimit] = useState(false);
  const [question, setQuestion] = useState('');
  const [qTitle, setQTitle] = useState('');
  const { addQuestion, addQuestionTitle } = useQuestions();


  const submit = e => {
    e.preventDefault();
    addQuestion(qCount,question);
    setQCount(qCount+1);
    setQuestion('');
  }

  const handleChange = e => {
    e.preventDefault(); 
    setQTitle(e.target.value);
    addQuestionTitle(e.target.value);
    if(e.target.value.length >= 80){

      setTypeLimit(true);

    }
  }

  return (
    <>
      <div>
        <input type="text" value={qTitle} onChange={handleChange} maxLength="80" disabled={typeLimit} placeholder="What is the value of Ï€?"/>
        <br />
        <input type="text" value={question} placeholder="Type an answer" maxLength="80" onChange={e => setQuestion(e.target.value)} required />
        <button onClick={submit}>Add</button>
      </div>
    </>

  )
}
