import React, {useState} from 'react';
import { useQuestions } from '../questionsComponent/questions';
import './vote.scss';


const Vote = () => {
  const { questions, qTitle } = useQuestions()

  let questionTitle = qTitle;

  let qArray = [];

  questions.forEach(item => {

    qArray.push(item.question)

  });

  useEffect(() => { console.log(questions) }, [questions]);


  return ( 
    <div className="vote-wrapper">
        <h3>{questionTitle}</h3>

        {qArray.map((item, id) => 
          <div key={id} className="vote-option">
            <input id={item+'_'+id} name="vote" type="radio"/>
            <label htmlFor={item+ '_' +id}>{item}</label>
          </div>
        )} 

    </div>
  )
}



export default Vote;
